import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';
import React from 'react';

export const BookList = (props) => {
    const [book, setBook] = useState({
        isbn: '',
        title: '',
        author: '',
        price: ''
    })

    const [books, setBooks] = useState([])

    const handleFieldChange = (e) => {
        setBook({ ...book, [e.target.name]: e.target.value })
    }

    const handleOnSubmit = (e) => {
        setBooks([...books, book])
        e.preventDefault();
    }

    return (
        <div>
            <h3>Please enter your personal information</h3>
            <div>
            isbn
                <input
                    type="text"
                    placeholder="Text Data"
                    name="isbn"
                    value={book.isbn}
                    onChange={handleFieldChange} />
            </div>
            <div>
            isbn
                <input
                    type="text"
                    placeholder="Text Data"
                    name="title"
                    value={book.title}
                    onChange={handleFieldChange} />
            </div>
            <div>
            author
                <input
                    type="text"
                    placeholder="Text Data"
                    name="author"
                    value={book.author}
                    onChange={handleFieldChange} />
            </div>
            <div>
            price
                <input
                    type="text"
                    placeholder="Text Data"
                    name="price"
                    value={book.price}
                    onChange={handleFieldChange} />
            </div>
            <button onClick={handleOnSubmit}>Add</button>
            <br/>
            <div>
                <div>
                    <span>isbn</span><span>title</span><span>author</span><span>price</span>
                </div>
                {
                    <div>
                    books.map(x => 
                        <span>{x.isbn}</span><span>title</span><span>author</span><span>price</span>
                    )
                    </div>
                }
            </div>
        </div>
    );
};
