import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';
import React from 'react';

export const Book2List = (props) => {

    let books = props.books
    let books2 = props.books2

    const removeBook = (e) => {
        let item = books.filter(x => x.isbn == e.target.value)
        props.setBooks2([...books2, ...item])

        let newBooks = books.filter(x => x.isbn !== e.target.value)
        props.setBooks(newBooks)

        e.preventDefault();
    }

    const removeBook2 = (e) => {
        let newBooks = books2.filter(x => x.isbn == e.target.value)
        props.setBooks([...books, ...newBooks])

        let items = books2.filter(x => x.isbn !== e.target.value)
        props.setBooks2(items)



        e.preventDefault();
    }
    const addClick = (e) => {
        props.history.push("/book2add")
    }

    return (
        <div>

            <button onClick={addClick}>Add</button>
            <br />
            <div>
                <h1> Book List</h1>
                <div margin="1">
                    <span>isbn</span>&nbsp;<span>title</span>&nbsp;<span>author</span>&nbsp;<span>price</span>
                </div>
                {

                    books.map(x => (
                        <div>
                            <span>{x.isbn}</span>&nbsp;<span>{x.title}</span>&nbsp;
                            <span>{x.author}</span>&nbsp;<span>{x.price}</span>
                            &nbsp; <span><button value={x.isbn} onClick={removeBook}>Remove</button></span>
                        </div>
                    ))
                }
            </div>

            <div>
                <h1> Removed List</h1>
                <div>
                    <span>isbn</span>&nbsp;<span>title</span>&nbsp;<span>author</span>&nbsp;<span>price</span>
                </div>
                {

                    books2.map(x => (
                        <div>
                            <span>{x.isbn}</span>&nbsp;<span>{x.title}</span>&nbsp;
                            <span>{x.author}</span>&nbsp;<span>{x.price}</span>
                            &nbsp; <span><button value={x.isbn} onClick={removeBook2}>Remove</button></span>
                        </div>
                    ))
                }
            </div>
        </div>
    );
};
