import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';
import React from 'react';

export const Book2List = (props) => {
    const [book, setBook] = useState({
        isbn: '',
        title: '',
        author: '',
        price: ''
    })

    const [books, setBooks] = props.location.state.books;

    const handleFieldChange = (e) => {
        setBook({ ...book, [e.target.name]: e.target.value })
    }

    const addClick = (e) => {
        props.history.push("/book2add")
    }

    const removeBook = (e) => {
        let newBooks = books.filter(x => x.isbn !== e.target.value)
        setBooks(newBooks)
        e.preventDefault();
    }
    return (
        <div>
            <h3>Please enter your personal information</h3>
            <div>
            isbn
                <input
                    type="text"
                    placeholder="Text Data"
                    name="isbn"
                    value={book.isbn}
                    onChange={handleFieldChange} />
            </div>
            <div>
            isbn
                <input
                    type="text"
                    placeholder="Text Data"
                    name="title"
                    value={book.title}
                    onChange={handleFieldChange} />
            </div>
            <div>
            author
                <input
                    type="text"
                    placeholder="Text Data"
                    name="author"
                    value={book.author}
                    onChange={handleFieldChange} />
            </div>
            <div>
            price
                <input
                    type="text"
                    placeholder="Text Data"
                    name="price"
                    value={book.price}
                    onChange={handleFieldChange} />
            </div>
            <button onClick={handleOnSubmit}>Add</button>
            <br/>
            <div>
                <h1> Book List</h1>
                <div margin="1">
                    <span>isbn</span>&nbsp;<span>title</span>&nbsp;<span>author</span>&nbsp;<span>price</span>
                </div>
                {
                    
                    books.map(x => (
                        <div>
                        <span>{x.isbn}</span>&nbsp;<span>{x.title}</span>&nbsp;
                        <span>{x.author}</span>&nbsp;<span>{x.price}</span>
                        &nbsp; <span><button value={x.isbn} onClick={removeBook}>Remove</button></span>
                        </div>
                    ))
                }
            </div>
        </div>
    );
};
