import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';


export const Login = (props) => {
    const cleanUser = {username: "", password: ""};

    const [user, setUser] = useState(cleanUser);

    const dispatch = useDispatch();

    let handleFieldChange = (event) => {
        setUser({...user, [event.target.name]: event.target.value})
    }
    let createAccount = (e) => {
        e.preventDefault();
        handleOnSubmit();
    }

    const handleOnSubmit = () => {
        dispatch({ type : 'createAccount' });
        props.history.push("/login",
            user);
    }

    return (
        <div>
            <h1>Login</h1>
            <table>
                <tbody>
                <tr>
                    <tr>Username</tr>
                    <td>
                        <input
                            type="text"
                            placeholder="Username"
                            name="username"
                            value={user.username}
                            onChange={handleFieldChange} />

                    </td>
                </tr>
                <tr>
                    <tr>Password</tr>
                    <td>
                        <input
                            type="text"
                            placeholder="password"
                            name="password"
                            value={user.password}
                            onChange={handleFieldChange} />

                    </td>
                </tr>
                <tr><td><button type="button" onClick={createAccount}>createAccount</button></td></tr>
                </tbody>
            </table>
        </div>
    );
};
