import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';

export const Login = (props) => {
    const cleanuser = {username: "", password: ""};
    const [account, setAccount] = useState('');

    const loginAccount = useSelector(state => state.loginAccount);

    const dispatch = useDispatch();

    let handleFieldChange = (event) => {
        setAccount({...account, [event.target.name]: event.target.value})
    }

    let loginHandler = (e) => {
        e.preventDefault();
        const isValid = loginValidation();
        if (!isValid)
            return;

        handleOnLogin();
    }

    let createAccount = (e) => {
        e.preventDefault();
        const isValid = formValidation();
        if (!isValid)
            return;

        handleOnSubmit();
    }

    const handleOnSubmit = () => {
        dispatch({ type : 'CREATE_ACC', account: account });
        props.history.push("/login",
        account);
    }

    const handleOnLogin = () => {
        dispatch({ type : 'LOGIN', loginAccount: account });
        props.history.push("/login", account);
    }
    const [usernameError, setUsernameError] = useState("");
    const [passwordError, setPasswordError] = useState("");

    const formValidation = () => {
        const usernameErr = {};
        const passwordErr = {};

        let isValid = true;
        if (account.username.trim().length < 5 || account.username.trim().length > 10) {
            usernameErr.shortLen = "Username needs to be between 5 and 10 characters long";
            isValid = false;
        }
        if (account.password.trim().length < 5) {
            passwordErr.longErr = "Password should be at least 5 characters long ";
            isValid = false;
        }

        setUsernameError(usernameErr);
        setPasswordError(passwordErr);

        return isValid;
    };

    const [loginError, setLoginError] = useState("");

    const loginValidation = () => {
        const loginErr = {};

        let isValid = true;
        if (loginAccount.username == '') {
            loginErr.shortLen = "Invalid username or password";
            isValid = false
            setLoginError(loginErr);
        }

        return isValid
    }

    return (
        <div>
            <h1>Create Account</h1>
            <div>
            {Object.keys(loginError).map((key) => {
                                return <div style={{ color: "red" }}>{loginError[key]}</div>;
                            })}

            </div>
            <table>
                <tbody>
                    <tr>
                        <td>Username</td>
                        <td>
                            <input
                                type="text"
                                placeholder="Username"
                                name="username"
                                value={account.username}
                                onChange={handleFieldChange} />
                            {Object.keys(usernameError).map((key) => {
                                return <div style={{ color: "red" }}>{usernameError[key]}</div>;
                            })}
                        </td>
                    </tr>
                    <tr>
                        <td>Password</td>
                        <td>
                            <input
                                type="text"
                                placeholder="password"
                                name="password"
                                value={account.password}
                                onChange={handleFieldChange} />
                            {Object.keys(passwordError).map((key) => {
                                return <div style={{ color: "red" }}>{passwordError[key]}</div>;
                            })}
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <button type="button" onClick={loginHandler}>Login</button>
                        </td>
                        <td>
                            <button type="button" onClick={createAccount}>createAccount</button>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
};
