import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';
import { SayHello } from '../lab09/SayHello';
import { Cal1 } from '../lab09/Cal1';
import { Cal2 } from '../lab09/Cal2';
import { Calculator } from '../lab12/Calculator';
import { Contacts } from '../lab12/Contacts';

export const EnterName = (props) => {
    const cleanuser = { username: "", password: "" };
    
    const [account, setAccount] = useState(cleanuser);

    const dispatch = useDispatch();

    let handleFieldChange = (event) => {
        setAccount({ ...account, [event.target.name]: event.target.value })
    }

    let nextHandler = (e) => {
        handleOnSubmit();
    }

    const handleOnSubmit = () => {
        dispatch({ type: 'CREATE_ACC', username: account.username });
        props.history.push("/productlist");
    }    

    return (
        <div>
            <h1>Create Account</h1>
            <div>&nbsp;Enter Your Name
                <input
                    type="text"
                    placeholder="username"
                    name="username"
                    value={account.username}
                    onChange={handleFieldChange} />
            
                <button type="button" id='nextHandler' 
                    onClick={handleOnSubmit}>Next</button>
            </div>
           
        </div>
    );
};
