import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';

export const AddProductName = (props) => {
    const username = useSelector(state => state.username);
    const cleanproduct = {productnumber: "", name: "", price: ""};

    let product = props.location.state.product

    const dispatch = useDispatch();

    let handleFieldChange = (event) => {
        setProduct({ ...product, [event.target.name]: event.target.value })
    }

    let addProduct = (e) => {
        props.history.push("/addproductnumber", product);
    }


    return (
        <div>
            <h1>Enter Product Number</h1>
            <div>
                User:  &nbsp; {username}
            </div>
            <div>
                Product Number:  &nbsp; {product.productnumber}
            </div>
            <div>
                Product Number:  <input
                    type="text"
                    placeholder="name"
                    name="name"
                    value={product.name}
                    onChange={handleFieldChange} />
            </div>
            <div>
                <button id="login" type="button" onClick={addProduct}>Next</button>

            </div>

        </div>
    );
};
