import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';
import React from 'react';

export const Book2List = (props) => {

    let books = props.books
    let books2 = useState([])
    
    const removeBook = (e) => {
        let newBooks = books.filter(x => x.isbn !== e.target.value)
        props.setBooks(newBooks)
        e.preventDefault();
    }

    const addClick = (e) => {
        props.history.push("/book2add")
    }

    return (
        <div>
            
            <button onClick={addClick}>Add</button>
            <br/>
            <div>
                <h1> Book List</h1>
                <div margin="1">
                    <span>isbn</span>&nbsp;<span>title</span>&nbsp;<span>author</span>&nbsp;<span>price</span>
                </div>
                {
                    
                    books.map(x => (
                        <div>
                        <span>{x.isbn}</span>&nbsp;<span>{x.title}</span>&nbsp;
                        <span>{x.author}</span>&nbsp;<span>{x.price}</span>
                        &nbsp; <span><button value={x.isbn} onClick={removeBook}>Remove</button></span>
                        </div>
                    ))
                }
            </div>

            <div>
                <h1> Removed List</h1>
                <div margin="1">
                    <span>isbn</span>&nbsp;<span>title</span>&nbsp;<span>author</span>&nbsp;<span>price</span>
                </div>
                {
                    
                    books.map(x => (
                        <div>
                        <span>{x.isbn}</span>&nbsp;<span>{x.title}</span>&nbsp;
                        <span>{x.author}</span>&nbsp;<span>{x.price}</span>
                        &nbsp; <span><button value={x.isbn} onClick={removeBook}>Remove</button></span>
                        </div>
                    ))
                }
            </div>
        </div>
    );
};
