import { createStore } from 'redux';

const initalstate = {accountList: [], loginAccount: {} };

const counterReducer = (state = initalstate, action) => {
  if (action.type === 'CREATE_ACC') {
    return {
      accountList: state.accountList.concat(action.account), 
      loginAccount: state.loginAccount
    };
  }

  if (action.type === 'LOGIN') {
    let matchedAccount = state.accountList.filter(x => x.username === action.loginAccount.username 
      && x.password === action.loginAccount.password);

    if (matchedAccount.length == 0)
      return {
        accountList: state.accountList,
        loginAccount: state.loginAccount
      };
    else
      return {
        accountList: state.accountList,
        loginAccount: matchedAccount
      };
  }
  return state;

}

const store = createStore(counterReducer, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());

export default store;
