import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';

export const ProductList = (props) => {
    const loginAccount = useSelector(state => state.loginAccount);
    const accountList = useSelector(state => state.accountList);

    const cleanuser = {username: "", password: ""};
    const [account, setAccount] = useState(cleanuser);

    const dispatch = useDispatch();

    let handleFieldChange = (event) => {
        setAccount({...account, [event.target.name]: event.target.value})
    }

    let loginHandler = (e) => {

        dispatch({ type : 'LOGIN', loginAccount: account })
        props.history.push("/welcome", account);
    }
    
    let createAccount = (e) => {
        handleOnSubmit();
    }

    const handleOnSubmit = () => {
        dispatch({ type : 'CREATE_ACC', account: account });

        props.history.push("/login", account);
    }
    
    return (
        <div>
            <h1>Product List</h1>

            <table>
                <tbody>
                    <tr>
                        <td>Username</td>
                        <td>
                            <input
                                type="text"
                                placeholder="Username"
                                name="username"
                                value={account.username}
                                onChange={handleFieldChange} />
                           
                        </td>
                    </tr>
                    <tr>
                        <td>Password</td>
                        <td>
                            <input
                                type="text"
                                placeholder="password"
                                name="password"
                                value={account.password}
                                onChange={handleFieldChange} />
                            
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <button id="login" type="button" onClick={loginHandler}>Login</button>
                        </td>
                        <td>
                            <button id="createAccount" type="button" onClick={createAccount}>createAccount</button>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
};
