import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';


export const CreateAccount = (props) => {
    const cleanUser = {username: "", password: ""};
    const [user, setUser] = useState(cleanUser);

    let handleFieldChange = (event) => {
        setUser({...user, [event.target.name]: event.target.value})
    }
    let createAccount = (e) => {
        e.preventDefault();
        const isValid = formValidation();
    }

    const [usernameError, setUsernameError] = useState("");
    const [passwordError, setPasswordError] = useState("");

    const formValidation = () => {
        const usernameErr = {};
        const passwordErr = {};

        let isValid = true;
        if (user.username.trim().length < 2) {
            usernameErr.shortLen = "Isbn should be more than 2 character";
            isValid = false;
        }
        if (user.password.trim().length > 5) {
            passwordErr.longErr = "Isbn can't be greater than 5 character";
            isValid = false;
        }

        setIsbnError(isbnErr);
        setTitleError(titleErr);
        setAuthorError(authorErr);
        setPriceError(priceErr);
        return isValid;
    };
    return (
        <div>
            <h1>Create Account</h1>
            <table>
                <tbody>
                    <tr>
                        <tr>Username</tr>
                        <td>
                            <input
                                type="text"
                                placeholder="Username"
                                name="username"
                                value={user.username}
                                onChange={handleFieldChange} />
                        </td>
                    </tr>
                    <tr>
                        <tr>Password</tr>
                        <td>
                            <input
                                type="text"
                                placeholder="password"
                                name="password"
                                value={user.password}
                                onChange={handleFieldChange} />
                        </td>
                    </tr>
                    <tr><td><button type="button" onClick={createAccount}>createAccount</button></td></tr>
                </tbody>
            </table>
        </div>
    );
};
