import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';

export const CreateAccount = (props) => {
    const user = {username: "", password: ""};

    const dispatch = useDispatch();

    let handleFieldChange = (event) => {
        setUser({...user, [event.target.name]: event.target.value})
    }

    let createAccount = (e) => {
        e.preventDefault();
        const isValid = formValidation();
        if (!isValid)
            return;

        handleOnSubmit();
    }

    const handleOnSubmit = () => {
        dispatch({ type : 'createAccount', user: user });
        props.history.push("/login",
            user);
    }

    const [usernameError, setUsernameError] = useState("");
    const [passwordError, setPasswordError] = useState("");

    const formValidation = () => {
        const usernameErr = {};
        const passwordErr = {};

        let isValid = true;
        if (user.username.trim().length < 5 || user.username.trim().length > 10) {
            usernameErr.shortLen = "Username needs to be between 5 and 10 characters long";
            isValid = false;
        }
        if (user.password.trim().length < 5) {
            passwordErr.longErr = "Password should be at least 5 characters long ";
            isValid = false;
        }

        setUsernameError(usernameErr);
        setPasswordError(passwordErr);

        return isValid;
    };
    return (
        <div>
            <h1>Create Account</h1>
            <table>
                <tbody>
                    <tr>
                        <tr>Username</tr>
                        <td>
                            <input
                                type="text"
                                placeholder="Username"
                                name="username"
                                value={user.username}
                                onChange={handleFieldChange} />
                            {Object.keys(usernameError).map((key) => {
                                return <div style={{ color: "red" }}>{usernameError[key]}</div>;
                            })}
                        </td>
                    </tr>
                    <tr>
                        <tr>Password</tr>
                        <td>
                            <input
                                type="text"
                                placeholder="password"
                                name="password"
                                value={user.password}
                                onChange={handleFieldChange} />
                            {Object.keys(passwordError).map((key) => {
                                return <div style={{ color: "red" }}>{passwordError[key]}</div>;
                            })}
                        </td>
                    </tr>
                    <tr><td><button type="button" onClick={createAccount}>createAccount</button></td></tr>
                </tbody>
            </table>
        </div>
    );
};
